<?php	include_once($_SERVER['DOCUMENT_ROOT'] . '.../api/core/config.php');	include_once($_SERVER['DOCUMENT_ROOT'] . '.../api/core/database.php');		session_start();		$db = new database();		//Connect to Database	if( $db->connect($ROLES["remote"]) )		echo '';	else		echo 'Error Cannot Connect to Database';		//Check if User is in Lottery	$db->query("					SELECT * 					FROM users					WHERE username = '".$_SESSION['user']."'					");			$usr = $db->fetch_row_assoc();	?><?php 	if( $usr != null ){?>	<div align="center" class="responce">		<p>You Are Registered <br />					<?php				if( $usr['lottery'] == null){			?>				Lottery Number: Not Yet Drawn <br />			<?php				} else {			?>				Lottery Number: <?php echo $usr['lottery']; ?> <br />			<?php				}			?>										<a id="unregister" >Click Here to Unregister</a></p>	</div>	 <?php	}else{?>	<div align="center" class="responce">		<p>You Are Not Registered <br />		<a id="register" >Click Here to Register</a></p>			</div>	<?php	}	$db->close();	?><script>	$('#register').click( function() {	  		$.post( 'data/PHP/lotteryRegistration.php', { register: "register" },		  function( results ) {			  			  $( ".responce" ).empty().append( results );		  }		);				return false;	  	});	$('#unregister').click( function() {	  		$.post( 'data/PHP/lotteryRegistration.php', { unregister: "unregister" },		  function( results ) {			 			  $( ".responce" ).empty().append( results );		  }		);				return false;	  	});</script>