<?php	include_once($_SERVER['DOCUMENT_ROOT'] . '.../api/core/config.php');	include_once($_SERVER['DOCUMENT_ROOT'] . '.../api/core/database.php');		session_start();		$db = new database();		//Connect to Database	if( $db->connect($ROLES["remote"]) )		echo '';	else		echo 'Error Cannot Connect to Database';		//Check if User is in Lottery	$db->query("					SELECT * 					FROM users					WHERE username = '".$_SESSION['user']."'					");			$usr = $db->fetch_row_array();		//Login	if (isset($_REQUEST['register'])) {		$db->query("					INSERT INTO users (username, email)					VALUES ('".$_SESSION['user']."', '".$_SESSION['user']."@rpi.edu')					");	}		if (isset($_REQUEST['unregister'])) {		$db->query("					DELETE FROM users					WHERE username = '".$_SESSION['user']."'						");	}?><?php 	if( $usr != null ){?>	<div class="responce">You Are Registered: <A id="unregister" >Unregister</A></div>	 <?php	}else{?>	<div class="responce">Your Are Not Registered: <A id="register">Register</A></div>	<?php	}	$db->close();	?><script>	$('#register').click( function() {	  		$.post( 'data/PHP/lotteryRegistration.php', { register: "register" },		  function( results ) {			  			  $( ".responce" ).empty().append( results );		  }		);				return false;	  	});	$('#unregister').click( function() {	  		$.post( 'data/PHP/lotteryRegistration.php', { unregister: "unregister" },		  function( results ) {			 			  $( ".responce" ).empty().append( results );		  }		);				return false;	  	});</script>